import React, { Component } from "react";
import SubmitButton from "../SubmitButton/SubmitButton";
import "./ButtonBar.scss";
import { connect, sendMsg } from "../../api";
import App from "../../App";

class ButtonBar extends Component {

    constructor(props, setting, deletePost, newPost, publish, save, open) {
        super(props);
        this.state = {
            setting: setting,
            deletePost: deletePost,
            newPost: newPost,
            publish: publish,
            save: save,
            open: open
        };
        this.setting = this.setting.bind(this);
        this.newPost = this.newPost.bind(this);

    }

    render() {
        return (
            <div class="buttonBar">
                <div class="settingButton">
                    <SubmitButton text="Setting" onClick={this.state.setting} />
                </div>

                <div class="deleteButton">
                    <SubmitButton text="Delete" onClick={this.state.deletePost} />
                </div>
                <div class="newButton">
                    <SubmitButton text="New" onClick={this.state.newPost} />
                </div>
                <div class="publishButton">
                    <SubmitButton text="Publish" onClick={this.publish} />
                </div>
                <div class="saveButton">
                    <SubmitButton text="Save" onClick={this.save} />
                </div>
            </div>
        );
    }

    setting() {
        sendMsg("Setting");
        this.setState({ setting: !this.state.setting });
    }
    deletePost() {
        sendMsg("Delete");
    }
    newPost() {
        sendMsg("New");
    }
    publish() {
        sendMsg("Publish");
    }
    save() {
        sendMsg("Save");
    }
    open() {
        sendMsg("Open");
    }

};

export default ButtonBar;
