import React, { Component } from "react";
import "./App.css";
import { connect, sendMsg } from "./api";
import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import PostList from "./components/PostList/PostList"
import SubmitButton from "./components/SubmitButton";
import Tiptap from "./components/Tiptap/Tiptap";
import Editor from "./components/Editor/Editor";
import Setting from "./components/Setting/Setting";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      setting: false
    };
  }

  send() {
    console.log("hello");
    sendMsg("Posts");
  }

  render() {
    return (
      <div className="App">
        <Header />
        if (this.state.setting) {
          <Setting />
        }
        else{
          <PostList posts={this.state.posts} func={this.open} />
        }

        <Editor />
        <div class="settingButton">
          <SubmitButton text="Setting" onClick={this.setting} />
        </div>
        <div class="deleteButton">
          <SubmitButton text="Delete" onClick={this.deletePost} />
        </div>
        <div class="newButton">
          <SubmitButton text="New" onClick={this.newPost} />
        </div>
        <div class="publishButton">
          <SubmitButton text="Publish" onClick={this.publish} />
        </div>
        <div class="saveButton">
          <SubmitButton text="Save" onClick={this.save} />
        </div>

        <Footer />
      </div>
    );
  }

  todo() {
    console.log("todo");
  }

  setting() {
    sendMsg("Setting");
  }
  deletePost() {
    sendMsg("Delete");
  }
  newPost() {
    sendMsg("New");
  }
  publish() {
    sendMsg("Publish");
  }
  save() {
    sendMsg("Save");
  }
  open() {
    sendMsg("Open");
  }

  laodAllPosts() {

  }
  componentDidMount() {
    connect((msg) => {
      console.log(msg.data)
      if (msg.data === "New") {
        console.log("New Message")
        this.setState(prevState => ({
          posts: [...this.state.posts, msg]
        }))
      }
      else if (msg.data === "Delete") {
        this.setState(prevState => ({
          posts: [...this.state.posts.slice(0, -1)]
        }));
      }
      //console.log(this.state);
    });
  }


}
export default App;


