import React, { Component } from "react";
import "./App.css";
import { connect, sendMsg } from "./api";
import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import PostList from "./components/PostList/PostList"
import SubmitButton from "./components/SubmitButton";
import Tiptap from "./components/Tiptap/Tiptap";
import Editor from "./components/Editor/Editor";
import Setting from "./components/Setting/Setting";
import ButtonBar from "./components/ButtonBar/ButtonBar";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      setting: false
    };
    this.setting = this.setting.bind(this);
  }

  send() {
    console.log("hello");
    sendMsg("Posts");
  }

  render() {
    var content = null;
    if (this.state.setting) {
      content = this.showSetting()
    }
    else {
      content = this.showEditor()
    }
    return (
      <div className="App">
        <Header />
        {content}
        <PostList posts={this.state.posts} func={this.open} />
        <ButtonBar setting={this.setting} deletePost={this.deletePost} newPost={this.newPost}
          publish={this.publish} save={this.save} open={this.open}
        />
        <Footer />

      </div>
    );
  }
  showSetting() {
    return (
      <Setting />
    )
  }

  showEditor() {
    return (
      <Editor />
    )
  }

  todo() {
    console.log("todo");
  }

  setting() {
    sendMsg("Setting");
    this.setState({ setting: !this.state.setting });
  }
  deletePost() {
    sendMsg("Delete");
  }
  newPost() {
    sendMsg("New");
  }
  publish() {
    sendMsg("Publish");
  }
  save() {
    sendMsg("Save");
  }
  open() {
    sendMsg("Open");
  }

  laodAllPosts() {

  }
  componentDidMount() {
    connect((msg) => {
      console.log(msg.data)
      if (msg.data === "New") {
        console.log("New Message")
        this.setState(prevState => ({
          posts: [...this.state.posts, msg]
        }))
      }
      else if (msg.data === "Delete") {
        this.setState(prevState => ({
          posts: [...this.state.posts.slice(0, -1)]
        }));
      }
      //console.log(this.state);
    });
  }


}
export default App;


