import React, { useMemo } from "react";
import SimpleMDE, { SimpleMdeReact } from "react-simplemde-editor";
import "easymde/dist/easymde.min.css";
import EasyMDE from "easymde";

const MDEditor2 = (props) => {
    console.log(props.content)
    console.log("TEST")
    var md = customMarkdownParser(props.content);
    const customRendererOptions = useMemo(() => {
        return {
            previewRender() {
                return md;
            },
        }
    }, []);
    const spellCheckerOptions = useMemo(() => {
        return {
            spellChecker: false,
        }
    })
    return (
        <div className="editor">
            <SimpleMdeReact
                //value="Click on eye icon to see a custom preview"
                options={spellCheckerOptions}
            />
        </div>
    );
}

const MDEditor = new EasyMDE({
    previewRender: (plainText) => {
        customMarkdownParser(plainText); // Async method
    },
})

export default MDEditor;
